mapper.select:
select 

from appraise_info
where ID = #{id,jdbcType=INTEGER}
  and ORDER_ID = #{orderId,jdbcType=INTEGER}

mapper.delete:
delete from appraise_info
where ID = #{id,jdbcType=INTEGER}
  and ORDER_ID = #{orderId,jdbcType=INTEGER}

mapper.insert:
insert into appraise_info (ID, ORDER_ID, USER_NAME, 
  USER_MOBILE, START_ORDER_TIME, APPRAISE_TIME, 
  APPRAISE_CONTENT, APPRAISE_IMG, GRADE, 
  Enable_SHOW, APPRAISE_REPLY, REPLY_CONTENT
  )
values (#{id,jdbcType=INTEGER}, #{orderId,jdbcType=INTEGER}, #{userName,jdbcType=VARCHAR}, 
  #{userMobile,jdbcType=VARCHAR}, #{startOrderTime,jdbcType=TIMESTAMP}, #{appraiseTime,jdbcType=TIMESTAMP}, 
  #{appraiseContent,jdbcType=VARCHAR}, #{appraiseImg,jdbcType=VARCHAR}, #{grade,jdbcType=DOUBLE}, 
  #{enableShow,jdbcType=INTEGER}, #{appraiseReply,jdbcType=INTEGER}, #{replyContent,jdbcType=VARCHAR}
  )
