mapper.select:
select

from system_info
WHERE 1=1

  AND SYS_USER_NAME = #{sysUserName}


  AND SYS_USER_PASSWORD = #{sysUserPassword}


  AND SYS_USER_MOBILE = #{sysUserMobile}


  AND ROLE_ID = #{roleId}


  AND CREAT_TIME = #{createTime}


  AND UPDATE_TIME = #{updateTime}


  AND REMARKS = #{remarks}
